import React, { useState } from 'react'
import DeleteIcon from '@material-ui/icons/Delete'
import EditIcon from '@material-ui/icons/Edit'

export default function Tabledata(props) {
    const {groceries} = props
    const {onEdit} = props

    const [editable, seteditable] = useState(false)

    const afterEdit = (id)=>{

        setedit({state:!edit.state,id:id})

    }

    return (
        <>
        {
            groceries.map((data,i)=>
            <tr key={i}>
                <td  onBlur={(e)=>{onEdit(data.id,{item:e.target.innerText})}}>
                    {data.item}
                </td>
                <td contentEditable onBlur={(e)=>{onEdit(data.id,{units:e.target.innerText})}}>
                    {data.units}
                </td>
                <td contentEditable onBlur={(e)=>{onEdit(data.id,{unitPrice:e.target.innerText})}}>
                   {data.unitPrice}
                </td>
                <td>
                    &#8377;{  data.units * data.unitPrice}
                </td>
                { !editable?
                <td>
                    <span>
                    <DeleteIcon onClick={()=>props.onDelete(data.id)}/>
                    </span>
                   <span>
                   <EditIcon onClick={()=>afterEdit(data.id)}/>
                   </span>


                </td>:<td> <button onClick={()=>afterEdit('')}> Save </button></td>}
            </tr>
        )}


        </>
    )
}
