import React, { useState } from "react";
import DeleteIcon from "@material-ui/icons/Delete";
import EditIcon from "@material-ui/icons/Edit";

export default function Tabledata({ data, onDelete, onEdit }) {
  const [editable, seteditable] = useState(false);

  const [updatedValue, setupdatedValue] = useState({
      item:null,
      units:null,
      unitPrice:null,
  })

  const handle = (e,fieldName)=>{
        setupdatedValue({...updatedValue,[fieldName]:e.target.innerText})
  }

  return (
    <>
      <tr>
        <td contentEditable={editable} onBlur={(e)=>handle(e,"item")} >{data.item}</td>
        <td contentEditable={editable} onBlur={(e)=>handle(e,"units")} >{data.units}</td>
        <td contentEditable={editable} onBlur={(e)=>handle(e,"unitPrice")} >{data.unitPrice}</td>
        <td>&#8377;{data.units * data.unitPrice}</td>
        {!editable ? (
          <td>
            <span>
              <DeleteIcon onClick={() => onDelete(data.id)} />
            </span>
            <span>
              <EditIcon onClick={() => seteditable(true)} />
            </span>
          </td>
        ) : (
          <td>
            {" "}
            <button onClick={()=>onEdit(data.id,)}> Save </button>
          </td>
        )}
      </tr>
    </>
  );
}
