import React, { useState } from "react";
import DeleteIcon from "@material-ui/icons/Delete";
import EditIcon from "@material-ui/icons/Edit";

export default function Tabledata({ data, onDelete }) {
  const [editable, seteditable] = useState(false);

  return (
    <>
      <tr>
        <td contentEditable={editable} onChange>{data.item}</td>
        <td contentEditable={editable}>{data.units}</td>
        <td contentEditable={editable}>{data.unitPrice}</td>
        <td>&#8377;{data.units * data.unitPrice}</td>
        {!editable ? (
          <td>
            <span>
              <DeleteIcon onClick={() => onDelete(data.id)} />
            </span>
            <span>
              <EditIcon onClick={() => seteditable(true)} />
            </span>
          </td>
        ) : (
          <td>
            {" "}
            <button> Save </button>
          </td>
        )}
      </tr>
    </>
  );
}
