import React from 'react'

export default function Total(props) {

    const {groceries} = props
    console.log(groceries);
    const value = groceries.reduce((acc,next)=>{
        acc+=next.units*next.unitPrice
        return acc
    },0)
    console.log(value);
    return (

        <>
            <span> Grand Total: &#8377; {value}</span>
        </>
    )
}
